"""
This script adds all issues of in-scope HTTP requests and responses to the table above.
"""

# Do the initial setup
if ref == 1 or "ScanIssues" not in session:
	header = ["Ref.", "Host", "URL", "Type", "Issue Name", "Issue Confidence"]
	session["ScanIssues"] = {}
	session["Severities"] = {"Information": "1 - Information", "Low": "2 - Low", "Medium": "3 - Medium", "High": "4 - High"}
	for issue in callbacks.getScanIssues(None):
		url_str = unicode(issue.getUrl())
		if callbacks.isInScope(issue.getUrl()):
			if url_str not in session["ScanIssues"]:
				session["ScanIssues"][url_str] = [issue]
			else:
				session["ScanIssues"][url_str].append(issue)

# Process only in-scope HTTP responses
if in_scope:
	url_str = unicode(url)
	if url_str in session["ScanIssues"]:
			issues = session["ScanIssues"][url_str]
			host = get_hostname(url)
			for issue in issues:
				severity = session["Severities"][issue.getSeverity()] if issue.getSeverity() in session["Severities"] else """Update session["Severities"]"""
				rows.append([ref, host, url_str, issue.getIssueType(), issue.getIssueName(), severity, issue.getConfidence()])
